pragma solidity ^0.4.18;

import "./NewsToken.sol";
 
 
contract IToken {
    function balanceOf(address who) public view returns (uint256);
    function transfer(address to, uint256 value) public returns (bool);
}

contract NewsCrowdsale {
    
    address public token;
    
    uint public timeDeploy;
    uint public timeStartAuction; 
    uint public timeFinalizeAuction;  
     
    address public ownerWallet;
    
    uint numOf_AllDays;
    uint numOf_AuctionDays;
    uint numOf_BreakOverDays;
    uint amountSellPerDay;
    uint curDay;  

    uint decimalVar;
    uint breakOverCounter = 0;
    
    mapping (uint => uint) public dayAuction;
    mapping (uint => uint) public daysBreakOver;
    mapping (uint => uint) public dailyTotals;
    
    mapping (uint => mapping (address => uint)) public userContribution;
    mapping (uint => mapping (address => bool)) public claimed;
    
    event Buy (uint window, address user, uint amount);
    event Claim (uint window, address user, uint amount); 
    
    modifier auctionActive() {
        require(now >= timeStartAuction && now <= timeFinalizeAuction);
        _;
    } 
     
    function NewsCrowdsale() public { 
        
        numOf_AllDays = 160;
        numOf_AuctionDays = 10;
        curDay = 0; 
        decimalVar = 1 ether; 
          
        timeDeploy = now;
        dayAuction[1] = timeDeploy + 80 days; 
        timeStartAuction = dayAuction[1]; 
    }

    function setTokenAddress(address tokenAddress) public {
        require(token == address(0));
        token = tokenAddress;

        amountSellPerDay = IToken(token).balanceOf(this) / numOf_AllDays * decimalVar;
    }
     
    
    function initDays() public {
        require(timeFinalizeAuction == 0);
        
        uint i = 1;
        while (i < numOf_AllDays) {  
            
            uint j = 1;
            for (;j < numOf_AuctionDays; j++) {
                dayAuction[i + j] = dayAuction[i + j - 1] + 1 days; 
            }
            i += j;
            
            dayAuction[i] = dayAuction[i - 1] + 81 days; 
            daysBreakOver[breakOverCounter] = dayAuction[i-1] + 1 days;
            breakOverCounter++; 
        } 
        
        breakOverCounter = 0; 
        timeFinalizeAuction = dayAuction[numOf_AllDays] + 1 days;
    }
    
    function () payable external {
       buy();
    }  
    
    function buy() payable public auctionActive {  
        uint today = now; 

        setCurrentDay(today);
        assert(notPause(today));
 
        userContribution[curDay][msg.sender] += msg.value;
        dailyTotals[curDay] += msg.value; 
        
        ownerWallet.transfer(msg.value);
        
        Buy(curDay, msg.sender, msg.value);
    } 
    
    function setCurrentDay(uint today) private {
        while (today >= dayAuction[curDay + 1] && curDay < numOf_AllDays) {
            curDay++;
        }  
        if (curDay % numOf_AuctionDays != 0) {
            breakOverCounter = curDay / numOf_AuctionDays;
        } 
    }

    function notPause(uint _now) private returns(bool) {
        if (_now > daysBreakOver[breakOverCounter] && _now < dayAuction[curDay + 1] ) {
            return false;
        }
        return true;
    }
    
     function claim(uint day) public { 
        if (claimed[day][msg.sender] || dailyTotals[day] == 0) {
            return;
        }
        
        require(curDay > day);
        
        uint price        = amountSellPerDay / dailyTotals[day];
        uint userPersent  = price * userContribution[day][msg.sender];
        uint reward       = userPersent / decimalVar; 

        claimed[day][msg.sender] = true;
        IToken(token).transfer(msg.sender, reward);

        Claim(day, msg.sender, reward);
    } 

    function claimAll() external { 
        for (uint i = 1; i <= curDay; i++) {
            claim(i);
        } 
    }  

    function getQuantitySoldEveryDay() public view returns(uint) {
        return amountSellPerDay / numOf_AllDays / decimalVar;
    } 
    
    function getCurrentDay() public view returns(uint) {
        uint dayCounter = curDay;
        
        while (now > dayAuction[dayCounter + 1]) {
            dayCounter++;
        } 
        return dayCounter;
    }

    function getNumberOfAuctionDays() public view returns(uint) {
        return numOf_AllDays;
    }
}
